#!/usr/bin/env python3
from random import getrandbits

from Crypto.Util.number import *
from gmpy2 import gcd

flag = "CMISCCTF{????????????}"


def get_gen():
    p = getPrime(512)
    q = getPrime(512)
    n = p*q
    phi = (p-1)*(q-1)
    while True:
        e = getrandbits(1024)
        if gcd(e, phi) == 1:
            break
    return n, e


n, e = get_gen()
c = pow(bytes_to_long(flag), e, n)
print("n:",n)
print("e:",e)
print("c:",c)
